import { Component } from '@angular/core';
import {BsModalRef,BsModalService} from "ngx-bootstrap/modal";

@Component({
  selector: 'mono-repo-employee-lookup',
  templateUrl: './employee-lookup.component.html',
  styleUrls: ['./employee-lookup.component.css'],
})
export class EmployeeLookupComponent {
  modalRef!: BsModalRef;

  constructor(private modalService:BsModalService) {
  }
  openModal(){
    this.modalRef = this.modalService.show(ModalAddItemsComponent, config);
  }
}
<p>employee-lookup works!</p>

<div>
    <label>Enter Employee Id:</label>
    <input type="text" name="emplyeeId" id="employeeId">
    <button (click)="openModal()">Lookup</button>
</div>
