import { Component, Input } from '@angular/core';
import { Employee } from 'libs/common-lookup/Employee';
import { BsModalRef } from 'ngx-bootstrap/modal';
import { DataTransferService } from '../data-transfer-service.service';
import { EmployeeService } from '../employee-service.service';

@Component({
  selector: 'mono-repo-modal-lookup',
  templateUrl: './modal-lookup.component.html',
  styleUrls: ['./modal-lookup.component.css'],
})
export class ModalLookupComponent {

  @Input()
  dataTransferService!: DataTransferService;

  employees: Employee[] = [];

  constructor(public modalRef: BsModalRef, private employeeService: EmployeeService) {
  }
  getEmployee(){
    this.employees=this.employeeService.getEmployee();
  }
  dataTransfer(employee:Employee){
    this.dataTransferService.DataTransfer(employee);
    this.modalRef.hide();
  }
}
<div class="modal-header">
  <h5 class="modal-title" id="lookUpModal">Employee Details</h5>
  <button type="button" class="btn" aria-label="Close" (click)="modalRef.hide()">
      <span aria-hidden="true">×</span>
  </button>
</div>
<div class="modal-container">
  <div class="modal-body">
      <table>
        <tbody>
          <tr>
            <td>FirstName:</td>
            <td>
              <input type="text" name="employeeFirstName" id="employeeFirstName" required>
            </td>
          </tr>
          <tr>
            <td>LastName:</td>
            <td>
              <input type="text" name="employeeLastName" id="employeeLastName" required>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
</div>
<div>
    <div>
      <table class="table">
        <thead *ngIf="employees.length!=0">
          <tr>
            <th scope="col">Employee Id</th>
            <th scope="col">First Name</th>
            <th scope="col">Last Name</th>
          </tr>
        </thead>
        <tr *ngFor="let employee of employees">
          <td>{{employee.employeeId}}</td>
          <td>{{employee.firstName}}</td>
          <td>{{employee.lastName}}</td>
          <td><button (click)="dataTransfer(employee)">Select</button></td>
        </tr>
      </table>
    </div>
</div>
<div class="modal-footer">
  <input type="submit" class="btn btn-primary" (click)="getEmployee()">
  <button type="button" class="btn btn-primary" (click)="modalRef.hide()">Close</button>
</div>
